awk ' BEGIN{ tot_marks=0; max_marks=0; min_marks=101; class_avg=0; n=0; printf "\n*** Grade Report for the ABC course ***\n" } NR>=2{ tot_marks=$3+$4+$5; n++; if(tot_marks<min_marks) {min_marks=tot_marks} if(tot_marks>max_marks) {max_marks=tot_marks}; class_avg = class_avg + tot_marks; if(tot_marks>=95 && tot_marks<=100) { print $1" A" } else if(tot_marks>=90 && tot_marks<95) { print $1" A-" } else if(tot_marks>=85 && tot_marks<90) { print $1" B" } else if(tot_marks>=80 && tot_marks<85) {print $1" B-"} else if(tot_marks>=75 && tot_marks<80) {print $1" C"} else if(tot_marks>=70 && tot_marks<75) { print $1" C-" } else if(tot_marks>=60 && tot_marks<70) { print $1" D" } else { print $1" F" }  } END { class_avg=class_avg/n; printf "Number of students = %d",n; printf "\nHighest = %d",max_marks; printf "\nLowest = %d",min_marks; printf "\nClass average = %d",class_avg; printf "â€‹\n***End of Grade Report ***\n\n" } ' marks.txt
